xrx:service.xrx:body:
  {

      let $resultXml :=

				{$collection_id}
				{$node_name}
				{$first_character}
				false

					  { 

    					  for $entries in distinct-values($collection//cei:text//*[name()=$node_name]) order by $entries return 
                              if($first_character = "All") then
                                if(string($entries) eq "") then()
                                 else(

                                        {$entries}
                                        {count($collection//cei:text//*[name()=$node_name][. eq $entries])}

                                        {
                                            for $entrie in $collection//cei:text//*[name()=$node_name][. eq $entries]
                                            return {root($entrie)//atom:id/text()}
                                        }


                                  )
					    else(
                            if(string($entries) eq "") then()
                            else(
                                if(starts-with(upper-case(string($entries)), $first_character) ) then(

                                    {string($entries)}
                                    {count($collection//cei:text//*[name()=$node_name][. eq $entries])}

                                    {
                                     for $entrie in $collection//cei:text//*[name()=$node_name][. eq $entries]
                                     return {root($entrie)//atom:id/text()}
                                    }


                                )
                                else()
                                )
                            )
					  }	



    let $setSessionVariable_collectionSearchAsXml := session:set-attribute("_collectionSearchAsXml", $resultXml)

    let $resultJson := 
    	jsonx:object((
    		jsonx:pair(
    		jsonx:string("collection_id"),
    		jsonx:string($collection_id)
    		),
    		jsonx:pair(
    		  jsonx:string("node_name"),
    		  jsonx:string($node_name)
    		),
    		jsonx:pair(
    		 jsonx:string("first_character"),
    		 jsonx:string($first_character)
    		 ),
    		 jsonx:pair(
    		 jsonx:string("mode"),
    		 jsonx:string($resultXml//mode/text())
    		 ),
    		jsonx:pair(
    		  jsonx:string("results"),
    		  jsonx:array(
    		  for $result in $resultXml//result
    		   return jsonx:object((
			      jsonx:pair(
			      	jsonx:string("term"),
					jsonx:string($result/term)
					),
			      jsonx:pair(
			        jsonx:string("count"),
			        jsonx:string($result/count)
			      	),
			      jsonx:pair(
			       jsonx:string("ids"),
			       jsonx:array(
			        for $id in $result//id return jsonx:string( $id )
			        )
			       )
	       		))       
     	 	 )
      		)
   		 ))

    let $setSessionVariable_collectionSearchAsJson := session:set-attribute("_collectionSearchAsJson", $resultJson)


      return $resultJson

    }
