xrx:service.xrx:body:
{

let $atomids := 
   for $result in $collectionSearch_results//result[term/text() = $search_term]
	return $result/ids

 let $resultsXml := 
					{$collection_id}
					{$collection_path}
					{$search_term}
					{
					      for $id in $atomids//id/text()
    						let $tokenized_id := tokenize($id, "/")
    						let $file := concat($collection_path,"/", $tokenized_id[last()], ".cei.xml")
    						return doc($file)
    						}



let $setSessionVariable_collectionSearchCharters := session:set-attribute("_collectionSearchCharters", $resultsXml)

return $resultsXml

}
